function  ValidateEmpty(a,b,c){var d = true;var e = document.getElementById(a);var f = document.getElementById(b);
    if(e.value == ""){
          f.innerText = c;  
          d = true;
    }
    else
    {
        f.innerText = "";  
        d = false;
    }
    return d;
}
function ValidateForTextOnly(a, b,c)
{
    var e = 
        document.getElementById(a);
    var f = 
        document.getElementById(b);

    if(isNaN(e.value))
    {
        f.innerText = "";
    }
    else{
        f.innerText = c;
    }
}

function ValidateForNumberOnly(a, b,c)
{
    var g = false;

    var e = document.getElementById(a);
    var f = document.getElementById(b);

    if(isNaN(e.value))
    {
        f.innerText = c;
        g = false; 
    }
    else{
        f.innerText = "";
        g = true   ;

    }
    return g;
}

function ValidateForRange(a, b,c,Min, Max)
{
    
    var e = document.getElementById(a);
    var f = document.getElementById(b);
    var h =  parseInt(e.value);
    if(h < Min || h >Max )
    {
        f.innerText = c;
    }
    else{
        f.innerText = "";
    }
}